package Pattern;

public class Merfents {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	
		for(int i=1;i<=3;i++)
		{
			for(int j=3;j>i;j--)
			{
				System.out.print(" ");
			}
			for(int z=1;z<=i;z++)
			{
				System.out.print((i*2)+" ");
			}
			System.out.println();
		}
		
		
		
		

	}

}
